CREATE TABLE `df_document_descriptions` (
  `dds_id` bigint unsigned NOT NULL AUTO_INCREMENT,
  `dds_id_user` bigint unsigned NOT NULL,
  `dds_description` text COLLATE utf8mb4_unicode_ci,
  `dds_add_date` datetime NOT NULL,
  `dds_change_date` datetime NOT NULL,
  PRIMARY KEY (`dds_id`),
  KEY `dds_user` (`dds_id_user`),
  CONSTRAINT `dds_user` FOREIGN KEY (`dds_id_user`) REFERENCES `df_users` (`us_id`) ON DELETE RESTRICT ON UPDATE RESTRICT
) ENGINE=InnoDB AUTO_INCREMENT=16 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci COMMENT='Опис або короткий зміст документів';

