CREATE TABLE `df_document_types` (
  `dt_id` bigint unsigned NOT NULL AUTO_INCREMENT,
  `dt_id_user` bigint unsigned NOT NULL,
  `dt_name` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `dt_description` text COLLATE utf8mb4_unicode_ci,
  `dt_add_date` datetime NOT NULL,
  `dt_change_date` datetime NOT NULL,
  PRIMARY KEY (`dt_id`),
  KEY `dt_user` (`dt_id_user`),
  CONSTRAINT `dt_user` FOREIGN KEY (`dt_id_user`) REFERENCES `df_users` (`us_id`) ON DELETE RESTRICT ON UPDATE RESTRICT
) ENGINE=InnoDB AUTO_INCREMENT=15 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci COMMENT='Типи документів';

INSERT INTO `df_document_types` VALUES ('1', '1', 'Лист', 'Документ у формі листа', '2024-05-04 14:28:44', '2024-05-04 14:28:44');
